if(localStorage.getItem('name')){document.querySelector('#name').value=localStorage.getItem('name');}
document.querySelector('#name').onkeyup=function(){localStorage.setItem('name',this.value);}
if(localStorage.getItem('email')){document.querySelector('#email').value=localStorage.getItem('email');}
document.querySelector('#email').onkeyup=function(){localStorage.setItem('email',this.value);}